// taskForm
import React, { useState, useEffect } from "react";

function useTaskForm() {
  const [state, setState] = useState({
    showForm: false,
    formValues: {
      projectName: "",
      task: "",
      startDate: "",
      endDate: "",
    },
  });

  const { showForm, formValues } = state;

  function openForm() {
    setState((prevState) => ({
      ...prevState,
      showForm: true,
    }));
  }

return{state, showForm, formValues}
}

export default useTaskForm;
