import React, { useState, useEffect } from "react";

function Tasks() {
  const [tasks, setTasks] = useState([]);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [formValue, setFormValue] = useState("");

  useEffect(() => {
    const taskApi = "http://127.0.0.1:8090/api/collections/tasks/records/";

    fetch(taskApi)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to fetch data");
        }
        return response.json();
      })
      .then((data) => {
        setTasks(data.items);
      })
      .catch((error) => {
        setError(error.message);
      });
  }, []);

  function openForm() {
    setShowForm(true);
  }

  function closeForm() {
    setShowForm(false);
  }

  function handleSubmit(event) {
    event.preventDefault();
    console.log("Form submitted:", formValue);
  }

  return (
    <div className="col col-8 col-tasks">
      {error ? (
        <div>Error: {error}</div>
      ) : (
        <div className="card all-tasks-cards">
          <div className="card-header tasks-header">
            Tasks
            <div className="btn-group">
              <button
                className="btn btn-sm dropdown-toggle"
                type="button"
                data-bs-toggle="dropdown"
                aria-expanded="false"
              >
                ...
              </button>
              <ul className="dropdown-menu task-dropdown-menu">
                <li>
                  <a className="dropdown-item" href="#" onClick={openForm}>
                    Create Task
                  </a>
                </li>
                <button
                  type="button"
                  className="btn-close task-close"
                  aria-label="Close"
                  onClick={closeForm}
                ></button>
              </ul>
            </div>
            <div
              id="popupForm"
              className="popup"
              style={{ display: showForm ? "block" : "none" }}
            >
              <div className="popup-content">
                <span className="close" onClick={closeForm}>
                  &times;
                </span>
                <h2>Task</h2>
                <form id="taskForm" onSubmit={handleSubmit}>
                  <label for="project-name">Project Name:</label>
                  <input
                    type="text"
                    value={formValue}
                    onChange={(e) => setFormValue(e.target.value)}
                    required
                  ></input>
                  <label for="task">Task:</label>
                  <input                     type="text"
                    value={formValue}
                    onChange={(e) => setFormValue(e.target.value)}
                    required></input>
                  <label for="start-date">Start Date:</label>
                  <input
                    type="date"
                    id="start-date"
                    name="start-date"
                    required
                  ></input>
                  <label for="end-date">End Date:</label>
                  <input
                    type="date"
                    id="end-date"
                    name="end-date"
                    required
                  ></input>
                  <input type="submit" value="Submit"></input>
                </form>
              </div>
            </div>
          </div>
          <div className="card-body tasks-card-body">
            <table className="table table-hover align-middle">
              <thead>
                <tr>
                  <th scope="col">Task</th>
                  <th scope="col">Status</th>
                  <th scope="col">Start Date</th>
                  <th scope="col">End Date</th>
                  <th scope="col">Edit</th>
                  <th scope="col">Delete</th>
                </tr>
              </thead>
            </table>
            {tasks.map((task, index) => (
              <div key={index}>
                <td>{task.task}</td>
                <td>{task.project_name}</td>
                <td>{task.start_date}</td>
                <td>{task.end_date}</td>
                <td>
                  <input
                    type="radio"
                    id="html"
                    name="fav_language"
                    value="HTML"
                  ></input>
                </td>
                <td>
                  <input
                    type="radio"
                    id="html"
                    name="fav_language"
                    value="HTML"
                  ></input>
                </td>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default Tasks;
