import React, { useState, useEffect, useContext } from "react";
import useFetchTasks from "../hooks/useFetchTasks";
import useForm from "../hooks/useForm";

function Tasks({ valueFromUpperCard }) {
  const { tasks, error } = useFetchTasks();
  const {
    openForm,
    showForm,
    formValues,
    closeForm,
    handleSubmit,
    handleInputChange,
  } = useForm();

  return (
    <div className="col col-8 col-tasks">
      {error ? (
        <div>Error: {error}</div>
      ) : (
        <div className="card all-tasks-cards">
          <div className="card-header tasks-header">
            Tasks
            <div className="btn-group">
              {/* Rest of your code */}
            </div>
            {/* Rest of your code */}
          </div>
          <div className="card-body tasks-card-body">
            <table className="table table-hover align-middle">
              <thead>
                <tr>
                  <th scope="col">Task</th>
                  <th scope="col">Project Name</th>
                  <th scope="col">Start Date</th>
                  <th scope="col">End Date</th>
                  <th scope="col">Edit</th>
                  <th scope="col">Delete</th>
                </tr>
              </thead>
              <tbody> {/* Add the opening tbody tag */}
                {tasks.map((task, index) => {
                  if (valueFromUpperCard === task.project_name) {
                    return (
                      <tr key={index}>
                        <td>{task.task}</td>
                        <td>{task.project_name}</td>
                        <td>{task.start_date}</td>
                        <td>{task.end_date}</td>
                        <td>
                          <input
                            type="radio"
                            id="html"
                            name="fav_language"
                            value="HTML"
                          ></input>
                        </td>
                        <td>
                          <input
                            type="radio"
                            id="html"
                            name="fav_language"
                            value="HTML"
                          ></input>
                        </td>
                      </tr>
                    );
                  }
                  return null;
                })}
              </tbody> {/* Add the closing tbody tag */}
            </table>
          </div>
        </div>
      )}
    </div>
  );
}

export default Tasks;
