      } else {
        closedVal = closedVal + 1;
      }
    }
    return null;
  });

  return (
    <>
      <div className="graph">
        <div className="card all-graph-cards">
          <div className="card-header">Graph</div>
          <div className="card-body graph-card-body">
            <div
              class="line-graph-section"
              aria-label="graphs of timeline metrics"
            >
              <svg
                width="100%"
                height="100%"
                viewBox="0 0 42 42"
                className="donut"
                aria-label="line graph for timeline"
              >
                <path
                  class="line"
                  d="M5 20 L15 25 L25 10 L35 15"
                  fill="none"
                  stroke="#8DC7CD"
                  stroke-width="2"
                ></path>
              </svg>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Graph;
