// useFetchProjects.js
import { useState, useEffect } from "react";

function useFetchProjects() {
  const [state, setState] = useState({
    projects: null,
    loading: true,
    error: null
  });

  const {projects,loading,error} = state;

  useEffect(() => {
    const projectApi = "http://127.0.0.1:8090/api/collections/projects/records/";

    fetch(projectApi)
    .then((response) => {
      if (!response.ok) {
        throw new Error("Failed to fetch data");
      }
      return response.json();
    })
    .then((data) => {
      setState((prevState) => ({
        ...prevState,
        projects: data.items,
      }));
    })
    .catch((error) => {
      setState((prevState) => ({
        ...prevState,
        error: error.message,
      }));
    });
}, []);

  return { projects, loading, error };
}

export default useFetchProjects;
