// taskForm
import React, { useState, useEffect } from "react";

function taskForm() {
  const [states, setState] = useState({
    showForm: false,
    formValues: {
      projectName: "",
      task: "",
      startDate: "",
      endDate: "",
    },
  });

  const { showForm, formValues } = states;

function openForm() {
  setState((prevState) => ({
    ...prevState,
    showForm: true,
  }));
}


return{states}
}

export default taskForm;
