// useTaskForm
import React, { useState } from "react";

function useTaskForm() {
  const [state, setState] = useState({
    showForm: false,
    formValues: {
      projectName: "",
      task: "",
      startDate: "",
      endDate: "",
    }
  });

  const { showForm, formValues } = state;

  function openForm() {
    setState((prevState) => ({
      ...prevState,
      showForm: true,
    }));
  }

  function closeForm() {
    setState((prevState) => ({
      ...prevState,
      showForm: false,
    }));
  }

  return {state, setState, openForm, showForm, formValues, closeForm};
}

export default useTaskForm;
