// Graph.js
import React, { useState } from "react";
import useFetchTasks from "../hooks/useFetchTasks";

function Graph({ valueFromUpperCard }) {
  const { tasks, error } = useFetchTasks();

  tasks.map((task) => {
    if (valueFromUpperCard === task.project_name) {
      console.log(task.start_date);
      console.log(task.end_date);
    }
    return null;
  });

  const [hoveredCircle, setHoveredCircle] = useState(null);

  const handleCircleHover = (circleIndex) => {
    setHoveredCircle(circleIndex);
  };

  const handleCircleLeave = () => {
    setHoveredCircle(null);
  };

  return (
    <>
      <div className="graph">
        <div className="card all-graph-cards">
          <div className="card-header">Graph</div>
          <div className="card-body graph-card-body"></div>
          <div
            class="line-graph-section"
            aria-label="graphs of timeline metrics"
          >
            <svg
              width="100%"
              height="100%"
              viewBox="0 0 42 42"
              class="line-graph"
              aria-label="line graph for timeline"
            >
              <path
                class="line"
                d="M10 10 L10 10 L20 10"
                fill="none"
                stroke="#8DC7CD"
                stroke-width="1"
              ></path>
              <circle
                cx="10"
                cy="10"
                r="1"
                fill="#8DC7CD"
                onMouseEnter={() => handleCircleHover(0)}
                onMouseLeave={handleCircleLeave}
                opacity={hoveredCircle === 0 ? 0.7 : 1}
              ></circle>
              <circle cx="15" cy="10" r="1" fill="#8DC7CD"></circle>
              <circle cx="20" cy="10" r="1" fill="#8DC7CD"></circle>
            </svg>
          </div>
          <div>{task.start_date && <p>Start Date: {task.start_date}</p>}</div>
        </div>
      </div>
    </>
  );
}

export default Graph;
