import React, { useState, useEffect } from "react";

function Tasks() {
  const [tasks, setTasks] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const taskApi = "http://127.0.0.1:8090/api/collections/tasks/records/";

    fetch(taskApi)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to fetch data");
        }
        return response.json();
      })
      .then((data) => {
        setTasks(data.items);
      })
      .catch((error) => {
        setError(error.message);
      });
  }, []);

  return (
    <div className="col col-8 col-tasks">
      {error ? (
        <div>Error: {error}</div>
      ) : (
        <div className="card all-tasks-cards">
          <div className="card-header tasks-header">
            Tasks
            <div class="btn-group">
              <button
                class="btn btn-sm dropdown-toggle"
                type="button"
                data-bs-toggle="dropdown"
                aria-expanded="false"
              >
                ...
              </button>
              <ul class="dropdown-menu task-dropdown-menu">
                <li>
                  <a class="dropdown-item" href="#" onclick="openForm()">
                    Create Task
                  </a>
                </li>
                <button
                  type="button"
                  class="btn-close task-close"
                  aria-label="Close"
                  onclick="closeForm()"
                ></button>
              </ul>
            </div>
            <div id="popupForm" class="popup">
              <div class="popup-content">
                <span class="close" onclick="closeForm()">
                  &times;
                </span>
                <h2>Task</h2>
                <form id="taskForm">
                <label for="project-name">Project Name:</label>
                <input type="search" id="project-name" name="project-name" required></input>
                </form>
              </div>
            </div>
          </div>
          {tasks.map((task, index) => (
            <div key={index}>
              <div>{task.task}</div>
              <div>{task.project_name}</div>
              <div>{task.start_date}</div>
              <div>{task.end_date}</div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Tasks;
